import React, { useState, useEffect } from 'react';
import './styles/Calendar.css';
import logo from "./logo.png";
import axios from 'axios';

const Calendar = () => {
  const [currentYear, setCurrentYear] = useState(2024);
  const [currentMonth, setCurrentMonth] = useState(10);
  const [wearingHistory, setWearingHistory] = useState([]);
  const [clothes, setClothes] = useState([]);
  const [popupData, setPopupData] = useState(null); // Data for the popup
  const [isPopupOpen, setIsPopupOpen] = useState(false); // Popup visibility

  // Fetch wearing history and clothes when the component mounts
  useEffect(() => {
    const fetchWearingHistory = async () => {
      try {
        const response = await axios.get('http://localhost:5050/api/wearingHistory', {
          params: { UserId: 1 },
        });
        const data = response.data.map((item) => ({
          ...item,
          Date: new Date(item.Date),
        }));
        setWearingHistory(data);
      } catch (error) {
        console.error('Error fetching wearing history:', error);
      }
    };

    // const fetchClothes = async () => {
    //   try {
    //     const response = await axios.get('http://localhost:5050/api/clothes', {
    //       params: { UserId: 1 },
    //     });
    //     setClothes(response.data); // Assuming response.data contains the array of clothes
    //   } catch (error) {
    //     console.error("Error fetching clothes:", error);
    //   }
    // };
    // fetchClothes();

    fetchWearingHistory();
  }, []);

  const daysInMonth = new Date(currentYear, currentMonth + 1, 0).getDate();
  const firstDayOfMonth = new Date(currentYear, currentMonth, 1).getDay();

  const calendarDays = [];
  for (let i = 0; i < firstDayOfMonth; i++) {
    calendarDays.push(null);
  }
  for (let i = 1; i <= daysInMonth; i++) {
    const date = new Date(currentYear, currentMonth, i);
    const history = wearingHistory.find(
      (entry) => entry.Date.toDateString() === date.toDateString()
    );
    calendarDays.push({
      date,
      history,
    });
  }

  const handleNextMonth = () => {
    if (currentMonth === 11) {
      setCurrentMonth(0);
      setCurrentYear(currentYear + 1);
    } else {
      setCurrentMonth(currentMonth + 1);
    }
  };

  const handlePrevMonth = () => {
    if (currentMonth === 0) {
      setCurrentMonth(11);
      setCurrentYear(currentYear - 1);
    } else {
      setCurrentMonth(currentMonth - 1);
    }
  };

  const monthNames = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December',
  ];
  const currentMonthName = monthNames[currentMonth];

  // Open popup
  const openPopup = (day) => {
    setPopupData(day || { history: {}, date: null }); // Ensure popupData is not null
    setIsPopupOpen(true);
  };

  // Close popup
  const closePopup = () => {
    setIsPopupOpen(false);
    setPopupData(null);
  };

  // Handle save changes to wearing history
  const handleSave = async () => {
    if (!popupData) return; // Avoid saving if popupData is null

    const updatedData = {
      Date: popupData.date.toISOString().split('T')[0],
      UserId: 1,
      Cloth1: popupData.history?.Cloth1 || null,
      Cloth2: popupData.history?.Cloth2 || null,
      Cloth3: popupData.history?.Cloth3 || null,
      Cloth4: popupData.history?.Cloth4 || null,
      Cloth5: popupData.history?.Cloth5 || null,
    };

    try {
      await axios.put('http://localhost:5050/api/wearingHistory', updatedData);
      closePopup();
      // Refresh wearing history
      const response = await axios.get('http://localhost:5050/api/wearingHistory', {
        params: { UserId: 1 },
      });
      const data = response.data.map((item) => ({
        ...item,
        Date: new Date(item.Date),
      }));
      setWearingHistory(data);
    } catch (error) {
      console.error('Error updating wearing history:', error);
    }
  };

  return (
    <div className="closet-container">
      <header className="closet-header">
        <img className="logo" src={logo} alt="logo" />
        <nav className="navigation">
          <span className="nav-item">closet</span>
          <span className="nav-item">calendar</span>
          <span className="nav-item">today outfit</span>
        </nav>
      </header>

      <div className="calendar-header">
        <button className="nav-button" onClick={handlePrevMonth}>◀</button>
        <h2>{currentMonthName} {currentYear}</h2>
        <button className="nav-button" onClick={handleNextMonth}>▶</button>
      </div>

      <div className="calendar-grid">
        {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map((day) => (
          <div key={day} className="calendar-day-name">{day}</div>
        ))}
        {calendarDays.map((day, index) => (
          <div
            key={index}
            className={`calendar-day ${day?.history ? '' : 'placeholder'}`}
            onClick={() => day && openPopup(day)}
          >
            {day && (
              <>
                <span className="date">{day.date.getDate()}</span>
                {day.history && (
                  <div className="clothes-container">
                    {[1, 2, 3, 4, 5].map((num) => {
                      const imageKey = `Cloth${num}Image`;
                      const nameKey = `Cloth${num}Name`;
                      return (
                        day.history[imageKey] && (
                          <img
                            key={num}
                            src={day.history[imageKey]}
                            alt={day.history[nameKey]}
                            title={day.history[nameKey]}
                            className="clothing-item"
                          />
                        )
                      );
                    })}
                  </div>
                )}
              </>
            )}
          </div>
        ))}
      </div>

      {isPopupOpen && popupData && (
        <div className="popup-overlay" onClick={closePopup}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()}>
            <h3>Edit Clothes Worn on {popupData.date?.toLocaleDateString() || 'Unknown'}</h3>
            <form>
              {[1, 2, 3, 4, 5].map((num) => {
                const clothKey = `Cloth${num}`;
                return (
                  <div key={num} className="form-group">
                    <label>Clothing {num}</label>
                    <select
                      value={popupData.history?.[clothKey] || ''}
                      onChange={(e) => {
                        setPopupData({
                          ...popupData,
                          history: {
                            ...popupData.history,
                            [clothKey]: e.target.value,
                          },
                        });
                      }}
                    >
                      <option value="">None</option>
                      {clothes.map((cloth) => (
                        <option key={cloth.ClothId} value={cloth.ClothId}>
                          {cloth.ClothName}
                        </option>
                      ))}
                    </select>
                  </div>
                );
              })}
            </form>
            <button onClick={handleSave}>Save</button>
            <button onClick={closePopup}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Calendar;
