import React from 'react';
import { Link } from 'react-router-dom';
import './styles/Calendar.css';
import logo from "./logo.png";

const Calendar = () => {
  const year = 2025;
  const month = 9; // October (0-indexed)
  const daysInMonth = new Date(year, month + 1, 0).getDate();
  const firstDayOfMonth = new Date(year, month, 1).getDay();

  // Create placeholders for empty cells and add days in the month
  const calendarDays = [];
  for (let i = 0; i < firstDayOfMonth; i++) {
    calendarDays.push({ date: null }); // Empty placeholder
  }
  for (let i = 1; i <= daysInMonth; i++) {
    calendarDays.push({ date: i });
  }

  return (
    <div className="closet-container">
      {/* Header Section */}
      <header className="closet-header">
        <img className="logo" src={logo} alt="logo" />
        <nav className="navigation">
          <Link to="/closet" className="nav-item">closet</Link>
          <Link to="/calendar" className="nav-item active">calendar</Link>
          <span className="nav-item">today outfit</span>
        </nav>
      </header>

      {/* Calendar Header */}
      <div className="calendar-header">
        <h2>October 2025</h2>
      </div>

      {/* Calendar Grid */}
      <div className="calendar-grid">
        {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map((day) => (
          <div key={day} className="calendar-day-name">{day}</div>
        ))}
        {calendarDays.map((day, index) => (
          <div key={index} className={`calendar-day ${day.date ? '' : 'placeholder'}`}>
            {day.date}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Calendar;
