import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './styles/Calendar.css';
import logo from "./logo.png";
import axios from 'axios'; // Import Axios for API calls

const Calendar = () => {
  const gridSize = 35;
  const [history, setHistory] = useState([]); // State for fetched history
  const userId = 1; // Replace with dynamic user ID if needed

  // Fetch wearing history data from the API
  useEffect(() => {
    const fetchWearingHistory = async () => {
      try {
        const response = await axios.get('http://localhost:5050/api/wearingHistory', {
          params: { UserId: userId },
        });
        setHistory(response.data); // Update state with API response
      } catch (error) {
        console.error("Error fetching wearing history:", error);
      }
    };

    fetchWearingHistory();
  }, [userId]);

  // Create items to display
  const itemsToDisplay = history.length >= gridSize
    ? history
    : [
        ...history,
        ...Array.from({ length: gridSize - history.length }, (_, index) => ({
          Date: `placeholder-${index + 1}`, // Use Date as a key
          Cloth1Name: "",
          Cloth1Image: "",
          Cloth2Name: "",
          Cloth2Image: "",
          Cloth3Name: "",
          Cloth3Image: "",
          Cloth4Name: "",
          Cloth4Image: "",
          Cloth5Name: "",
          Cloth5Image: "",
        })),
      ];

  return (
    <div className="closet-container">
      {/* Header Section */}
      <header className="closet-header">
        <img className="logo" src={logo} alt="logo" />
        <nav className="navigation">
          <Link to="/closet" className="nav-item">closet</Link>
          <Link to="/calendar" className="nav-item">calendar</Link>
          <span className="nav-item">today outfit</span>
        </nav>
      </header>

      {/* Calendar Grid */}
      <div className="closet-grid">
        {itemsToDisplay.map((item, index) => (
          <div key={item.Date || `placeholder-${index}`} className="closet-item">
            <div className="item-date">{item.Date || "No Date"}</div>
            <div className="item-images">
              {item.Cloth1Image && <img src={item.Cloth1Image} alt={item.Cloth1Name} className="cloth-image" />}
              {item.Cloth2Image && <img src={item.Cloth2Image} alt={item.Cloth2Name} className="cloth-image" />}
              {item.Cloth3Image && <img src={item.Cloth3Image} alt={item.Cloth3Name} className="cloth-image" />}
              {item.Cloth4Image && <img src={item.Cloth4Image} alt={item.Cloth4Name} className="cloth-image" />}
              {item.Cloth5Image && <img src={item.Cloth5Image} alt={item.Cloth5Name} className="cloth-image" />}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Calendar;
