// src/Closet.js
import React from 'react';
import './styles/Closet.css';

const Closet = () => {
  const gridSize = 35;

  const clothes = [
    { id: 1, name: "T-Shirt", image: "/mockup_img/shirt.jpg", tag: "Summer" },
    { id: 2, name: "Jeans", image: "/mockup_img/pants.jpg", tag: "Casual" },
    { id: 3, name: "Jacket", image: "/mockup_img/shirt.jpg", tag: "Winter" },
    // Add more items if you want to test overflow
  ];

  let itemsToDisplay;

  if (clothes.length >= gridSize) {
    // If there are 35 or more items, just display clothes
    itemsToDisplay = clothes;
  } else {
    // If there are fewer than 35 items, add placeholders
    const emptyGridItems = Array.from({ length: gridSize - clothes.length }, (_, index) => ({
      id: clothes.length + index + 1,
      name: "",
      image: "",
      tag: ""
    }));
    itemsToDisplay = [...clothes, ...emptyGridItems];
  }

  return (
    <div className="closet-container">
      {/* Header Section */}
      <header className="closet-header">
        <div className="logo">Logo</div>
        <nav className="navigation">
          <span className="nav-item active">closet</span>
          <span className="nav-item">calendar</span>
          <span className="nav-item">today outfit</span>
        </nav>
      </header>

      {/* Search Section */}
      <div className="search-bar">
        <input type="text" placeholder="Search" />
        <button className="search-button">üîç</button>
      </div>

      {/* Closet Grid */}
      <div className="closet-grid">
        {itemsToDisplay.map((item) => (
          <div key={item.id} className="closet-item">
            <img src={item.image} alt={item.name} className="item-image" />
            <span className="item-name">{item.name}</span>
            <span className="item-tag">{item.tag}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Closet;
